mapper.select:
SELECT *
FROM production_management


		useful = #{useful}


		useful = 1


		AND moc_id Like #{moc_id}


		AND moc_ta011 Like #{moc_ta011}


		AND moc_ta006 Like #{moc_ta006}


		AND moc_ta006 not LIKE ALL(array[${moc_ta006_not}])


ORDER BY moc_week asc, moc_priority desc, moc_ta009 asc,moc_id asc

mapper.update:
UPDATE production_management SET bom_kind = #{bom_kind}, moc_note = #{moc_note}, moc_status = #{moc_status}, moc_priority = #{moc_priority}, ivn_note = #{ivn_note}, ivn_status = #{ivn_status}, ivn_items = #{ivn_items}, mpr_note = #{mpr_note}, mpr_date = #{mpr_date}, mes_note = #{mes_note},note = #{note},
moc_ta006= #{moc_ta006},moc_ta034= #{moc_ta034},moc_ta035= #{moc_ta035}, moc_ta015= #{moc_ta015},moc_ta017= #{moc_ta017},moc_ta009= #{moc_ta009}, moc_ta010= #{moc_ta010},moc_ta011= #{moc_ta011},cop_tc012= #{cop_tc012}, moc_ta029= #{moc_ta029},order_id= #{order_id},moc_week= #{moc_week},
sys_check_sum= #{sys_check_sum},useful= #{useful}, sys_modify_date= #{sys_modify_date}, sys_modify_user= #{sys_modify_user} WHERE moc_id = #{moc_id};
