mapper.select:
SELECT * FROM software_version
AND client_name like '%' || #{software_entity.client_name} || '%'
AND bom_product_id like '%' || #{software_entity.bom_product_id} || '%'
AND product_model_in like '%' || #{software_entity.product_model_in} || '%'
AND id = #{software_entity.id}
${software_limit}

mapper.update:
UPDATE software_version SET sys_modify_date = #{sys_modify_date}, sys_modify_user = #{sys_modify_user}, client_name = #{client_name}, product_model_in = #{product_model_in}, bom_id = #{bom_id}, bom_product_id = #{bom_product_id}, mb_ver = #{mb_ver}, mb_ver_ecn = #{mb_ver_ecn}, bios = #{bios}, ec = #{ec}, nvram = #{nvram}, os = #{os}, note = #{note}, note1 = #{note1}, note2 = #{note2}
AND id = #{id}
AND client_name = #{client_name} AND bom_product_id = #{bom_product_id}
